function CaptchaShit(input){
    var inputArray = input.split("");
    inputArray.splice(inputArray.length,0, inputArray[0]);
    var previousNum = 0;
    return inputArray.reduce(function(total,num){
        if(previousNum == num){
            previousNum = num;
            return Number(total) + Number(previousNum);
        }
        previousNum = num;
        return Number(total);
    }, 0);
}
