function CaptchaShit2(input){
     var inputArray = input.split("");
     var index = 0;
     return inputArray.reduce(function(total, num) {
        var cursorIndex = GetCursorIndex(index, inputArray.length);
        index++;
        var cursorNum = inputArray[cursorIndex];
       if (cursorNum == num) {
         return Number(total) + Number(cursorNum);
       }
       return Number(total);
     }, 0);
}
function GetCursorIndex(index, length) {
  return (index + length / 2) % length;
}
